
var req = require("superagent"),
    dialog = require("dialog"),
    dom = require("dom"),
    Domain = require("jsdm.proxy"),
    domain = new Domain("/domain"),
    write_form = dom(require("./template"));

var deltype = dom("#article_type_list button");
deltype.on("click",function(){
    var tid = dom(this).attr("typeid");
    domain.call("Navigation.remove","id001",[tid],function(){
        window.location.href = "/";
    })
})

var addtype = dom("#addtype");
addtype.on("click",function(){
    domain.call("Navigation.createNode","id001",["nihao"],function(){
        window.location.href = "/";
    })
})

dialog("title",write_form.els[0]).show();