module.exports = wrap;

function wrap(repos, services) {

    handle1.commandName = "create a article";
    function handle1(args, callback) {
        repos.Article.create(args,callback);
    }

    handle2.commandName = "init" ;
    function handle2(args,callback){
        setTimeout(function(){
            repos.Navigation.create(null,function(){});

            if(callback){
                callback();
            }

        },500)


    }

    handle3.commandName = "remove a article";
    function handle3(args,callback){
        setTimeout(function(){
           callback();
        },20)
        repos.Article.remove(args.id);
    }


    return [handle1,handle2,handle3];
}