
var Tree = require("../../tree-data"),
    dbs = require("../dbs"),
    tree = new Tree("id001");

module.exports = {
    nav:tree,
    articleByTime:function(skip,cb){
        var getDB = require("../dbs").getDB;
        var db = getDB('Article');
        db.find({}).desc("updateTime")
            .skip(skip)(function(err,rs){
            if(rs){
                cb(rs)
            }else{
                cb([]);
            }
        })
    },
    articleByType:function(type,skip,cb){
        var getDB = require("../dbs").getDB;
        var db = getDB('Article');
        db.find({type:type}).desc("updateTime")
            .limit(3)
            .skip(skip)(function(err,rs){
            if(rs){
                cb(rs)
            }else{
                cb([]);
            }
        })
    }
}



